	(3, '', '', '', ' Mitra Sent For Approval Laporan 3 Bulanan - Naratif 1', '<p>User : {NAMA_USER}<br />\r\nmitra : {NAMA_MITRA}<br />\r\nNo. kontrak : {NO_KONTRAK}<br />\r\nTanggal jatuh tempo : {TGL_JATUH_TEMPO}</p>\r\n', ''),
	(4, '', '', '', ' Mitra Sent For Approval Laporan 3 Bulanan - Naratif 2', '<p>User {NAMA_USER}<br />\r\nmitra {NAMA_MITRA}&nbsp;<br />\r\nNo. kontrak : {NO_KONTRAK}<br />\r\nTanggal jatuh tempo : {TGL_JATUH_TEMPO}</p>\r\n', ''),
	(5, '', '', '', ' Komentar Laporan 3 Bulanan - Naratif 1 - Capaian Aktual', '<p>User : {NAMA_USER}&nbsp;<br />\r\nNo. Kontrak : {NO_KONTRAK}<br />\r\nTgl Jatuh Tempo : {TGL_JATUH_TEMPO}</p>\r\n\r\n<p><strong>Capaian Aktual:</strong><br />\r\n{CAPAIAN_AKTUAL}</p>\r\n\r\n<hr />\r\n<p><strong>Isi Komentar :</strong><br />\r\n{KOMENTAR}</p>\r\n', ''),
	(6, '', '', '', ' Komentar Laporan 3 Bulanan - Naratif 2', '<p>User {NAMA_USER} Komentar terhadap&nbsp;laporan 3 bulanan - naratif 2<br />\r\nNo. Kontrak : {NO_KONTRAK}<br />\r\nTgl Jatuh Tempo : {TGL_JATUH_TEMPO}</p>\r\n\r\n<p><strong>pertanyaan kunci&nbsp;:</strong><br />\r\n{PERTANYAAN_KUNCI}</p>\r\n\r\n<p><strong>Jawaban pertanyaan kunci&nbsp;:</strong><br />\r\n{JAWABAN}</p>\r\n\r\n<hr />\r\n<p><strong>Isi Komentar :</strong><br />\r\n{KOMENTAR}</p>\r\n', ''),
	(7, '', '', '<p>User {NAMA_USER} Komentar terhadap&nbsp;laporan 3 bulanan - naratif 1&nbsp;<br />\r\nNo. Kontrak : {NO_KONTRAK}<br />\r\nTgl Jatuh Tempo : {TGL_JATUH_TEMPO}<br />\r\nCapaian aktual :</p>\r\n\r\n<p>{CAPAIAN_AKTUAL}</p>\r\n\r\n<hr />\r\n<p>Isi Komentar :</p>\r\n\r\n<p>{KOMENTAR}</p>\r\n', ' Komentar Laporan 3 Bulanan - Naratif 1 - Capaian Keseluruhan', '<p>User : {NAMA_USER}&nbsp;<br />\r\nNo. Kontrak : {NO_KONTRAK}<br />\r\nTgl Jatuh Tempo : {TGL_JATUH_TEMPO}</p>\r\n\r\n<p><strong><span style="line-height:1.6em">Capaian Keseluruhan:</span></strong><br />\r\n<span style="line-height:1.6em">{CAPAIAN_KESELURUHAN}</span></p>\r\n\r\n<hr />\r\n<p><strong>Isi Komentar :</strong><br />\r\n<span style="line-height:1.6em">{KOMENTAR}</span></p>\r\n', ''),
	(8, '', '', '', 'Jatuh Tempo Laporan Naratif 1 No. Kontrak {NO_KONTRAK}', '<p>Dear Mitra {NAMA_MITRA},</p>\r\n\r\n<p>Laporan Naratif 1 dengan nomor Kontrak {NO_KONTRAK} akan jatuh tempo pada tanggal {TGL_JATUH_TEMPO}. Mohon segera kirim laporan tersebut kepada The Asia Foundation.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Terima kasih,</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>The Asia Foundation</strong></p>\r\n', ''),
	(9, '', '', '', 'Jatuh Tempo Laporan Naratif 2 - No. Kontrak {NO_KONTRAK}', '<p>Dear Mitra {NAMA_MITRA},</p>\r\n\r\n<p>Laporan Naratif 2 dengan nomor Kontrak {NO_KONTRAK} akan jatuh tempo pada tanggal {TGL_JATUH_TEMPO}. Mohon segera kirim laporan tersebut kepada The Asia Foundation.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Terima kasih,</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>The Asia Foundation</strong></p>\r\n', ''),
