	(26, 'Mitra sent for approval TAF 209', '', '', '{MITRA} - Sent for approval  TAF 209', '<div>\r\n<div>\r\n<p>Dear TAF,</p>\r\n\r\n<p>Data program : &nbsp;{DATA_PROGRAM}<br />\r\nReport Ke : {REPORT_KE}<br />\r\nJenis Laporan&nbsp;: {JENIS_LAPORAN}<br />\r\nPeriode Pelaporan : {PERIODE}<br />\r\nJatuh Tempo : {JATUH_TEMPO}</p>\r\n\r\n<p>...........</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Terima kasih,</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>The Asia Foundation</strong></p>\r\n</div>\r\n</div>\r\n', '209MITRA'),
	(27, 'TAF Approve / Revise / Reject TAF 209', '', '', 'TAF Set Status TAF 209 To : {STATUS} ', '<p>Dear Mitra,</p>\r\n\r\n<p>Data program : &nbsp;{DATA_PROGRAM}<br />\r\nReport Ke : {REPORT_KE}<br />\r\nJenis Laporan&nbsp;: {JENIS_LAPORAN}<br />\r\nPeriode Pelaporan : {PERIODE}<br />\r\nJatuh Tempo : {JATUH_TEMPO}<br />\r\nStatus : {STATUS}<br />\r\n<span style="line-height:1.6em">Komentar : {KOMENTAR}</span></p>\r\n\r\n<p>...........</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Terima kasih,</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>The Asia Foundation</strong></p>\r\n', '209TAF'),
	(28, 'Reminder Laporan Narasi', '', '', 'Reminder Laporan Narasi PEKKA Segera Jatuh Tempo', '<p><span style="color: rgb(34, 34, 34); font-family: arial, sans-serif; font-size: 12.8px;">Yth</span>,&nbsp;Mitra AIPJ2 yang kami hormati,&nbsp;</p>\r\n\r\n<p><span style="color: rgb(34, 34, 34); font-family: arial, sans-serif; font-size: 12.8px;">Dengan ini kami informasikan bahwa:</span></p>\r\n\r\n<p>Laporan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; &nbsp; : {JENIS_LAPORAN} - {LAPORAN_KE}<br />\r\nLembaga&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;: {NAMA_MITRA}<br />\r\nPeriode Pelaporan : {PERIODE}<br />\r\n<br />\r\nAkan jatuh tempo pada&nbsp;{TGL_JATUH_TEMPO}</p>\r\n\r\n<p><span style="color: rgb(34, 34, 34); font-family: arial, sans-serif; font-size: 12.8px;">Mohon segera lengkapi laporan Anda terutama laporan kegiatan, siapkan dokumen pendukung relevan, dan kirimkan laporan 3 bulanan melalui sistem.<br />\r\n<br />\r\nMenu laporan tersebut bisa diakses dari panel di sebelah kiri -&gt; </span></p>\r\n\r\n<p><span style="color: rgb(34, 34, 34); font-family: arial, sans-serif; font-size: 12.8px;">LAPORAN MITRA AIPJ2 -&gt; LAPORAN NARASI -&gt; Pilih periode yang relevan.&nbsp;</span><br />\r\n&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style="color: rgb(34, 34, 34); font-family: arial, sans-serif; font-size: 12.8px;">Salam hangat</span>,<br />\r\nTim AIPJ2<br />\r\n&nbsp;</p>\r\n\r\n<p><span style="color: rgb(34, 34, 34); font-family: arial, sans-serif; font-size: 12.8px;">Laporan ini dikirim secara otomatis dari sistem MEL Online AIPJ2</span><span style="color: rgb(34, 34, 34); font-family: arial, sans-serif; font-size: 12.8px;">, mohon tidak dibalas. Bilamana ada pertanyaan, hubungi Admin</span></p>\r\n', ''),
	(29, 'Reminder Laporan Keuangan', '', '', 'Laporan Keuangan PEKKA Segera Jatuh Tempo', '<p><span style="color: rgb(34, 34, 34); font-family: arial, sans-serif; font-size: 12.8px;">Yth</span>, Mitra</p>\r\n\r\n<p><span style="color: rgb(34, 34, 34); font-family: arial, sans-serif; font-size: 12.8px;">Dengan ini kami informasikan bahwa:</span></p>\r\n\r\n<p>Laporan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;: {JENIS_LAPORAN} - {LAPORAN_KE}<br />\r\nLembaga&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;: {NAMA_MITRA}<br />\r\nPeriode Pelaporan : {PERIODE}<br />\r\nJatuh Tempo&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; : {TGL_JATUH_TEMPO}<br />\r\n<br />\r\nAkan jatuh tempo pada&nbsp;{TGL_JATUH_TEMPO}<br />\r\n<span style="color: rgb(34, 34, 34); font-family: arial, sans-serif; font-size: 12.8px;">Mohon segera lengkapi laporan Anda dan dokumen relevan, dan kirimkan melalui sistem.&nbsp;</span></p>\r\n\r\n<p><span style="color: rgb(34, 34, 34); font-family: arial, sans-serif; font-size: 12.8px;">Untuk laporan TAF209, &nbsp;pastikan Grant Officer telah menyetujui laporan excel tersebut sebelum melakukan rekap ke dalam sistem.&nbsp;</span><br />\r\n<br />\r\n&nbsp;</p>\r\n\r\n<p><span style="color: rgb(34, 34, 34); font-family: arial, sans-serif; font-size: 12.8px;">Salam hangat</span>,<br />\r\nTim AIPJ2<br />\r\n&nbsp;</p>\r\n\r\n<p><span style="color: rgb(34, 34, 34); font-family: arial, sans-serif; font-size: 12.8px;">Laporan ini dikirim secara otomatis dari sistem MEL Online AIPJ2, mohon tidak dibalas. Bilamana ada pertanyaan, hubungi contact person terkait. Bila Anda mengalami kesulitan teknis, hubungi Admin</span></p>\r\n', ''),
	(30, 'Reminder Kontrak Hendak Selesai', '', '', 'Periode Grant Segera Berakhir', '<p><span style="color: rgb(34, 34, 34); font-family: arial, sans-serif; font-size: 12.8px;">Yth</span>,&nbsp;Mitra</p>\r\n\r\n<p><span style="color: rgb(34, 34, 34); font-family: arial, sans-serif; font-size: 12.8px;">Dengan ini kami informasikan bahwa masa berlaku grant:</span></p>\r\n\r\n<p>Lembaga &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;: {NAMA_MITRA}<br />\r\nPeriode Program &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;: {PERIODE}<br />\r\n<span style="color: rgb(34, 34, 34); font-family: arial, sans-serif; font-size: 12.8px;">Akan berakhir tanggal</span> {TGL_JATUH_TEMPO}</p>\r\n\r\n<p><span style="color: rgb(34, 34, 34); font-family: arial, sans-serif; font-size: 12.8px;">Mohon segera selesaikan kegiatan yang belum terlaksana, dan bilamana Amendment diperlukan / perlu segera diproses, segera hubungi AIPJ.</span></p>\r\n\r\n<p>Salam hangat,</p>\r\n\r\n<p>Tim AIpJ</p>\r\n\r\n<p>Laporan ini dikirim dari sistem MEL Online AIPJ, mohon tidak dibalas. Bilamana ada pertanyaan, hubungi contact person terkait.</p>\r\n', ''),
	(31, 'Komentar Workplan', '', '', 'Komentar Workplan', '<p>User <strong>{NAMA_USER}</strong> submit comment On :&nbsp;</p>\r\n\r\n<p>Data Program : {DATA_PROGRAM}<br />\r\nWorkplan : {WORKPLAN}<br />\r\nBudget Estimate : {BUDGET}<br />\r\nLocation: {LOCATION}<br />\r\nDate: {DATE}</p>\r\n\r\n<p><strong>Comment:</strong></p>\r\n\r\n<p>{KOMENTAR}</p>\r\n', 'WP-COMMENT'),
	(32, 'Mitra Submit Monthly Report', '', '', 'Mitra {MITRA} Submit Monthly Report Period {MONTH}', '<p><span style="color: rgb(34, 34, 34); font-family: arial, sans-serif; font-size: 12.8px;">Yth</span>,&nbsp;AIPJ</p>\r\n\r\n<p><span style="color: rgb(34, 34, 34); font-family: arial, sans-serif; font-size: 12.8px;">User {USERNAME} dari mitra {MITRA} submit monthly report {MONTH} data program {NAMA_PROGRAM}</span></p>\r\n\r\n<p>klik <a href="{LINK}">disini</a></p>\r\n', 'MONREPMITRA'),
	(33, 'Comment Mitra Monthly Report', '', '', 'User {USERNAME} comment on Mitra Monthly Report', '<p><span style="color: rgb(34, 34, 34); font-family: arial, sans-serif; font-size: 12.8px;">User {USERNAME} ({GROUP}) comment on monthly report {MONTH} data program {NAMA_PROGRAM}</span></p>\r\ncomment : \r\n<br>\r\n{COMMENT}', 'MONREPCOMMENT'),
	(34, 'Comment Mitra Quarterly Report', '', '', 'User {USERNAME} comment on Mitra Quarterly Report', '<p><span style="color: rgb(34, 34, 34); font-family: arial, sans-serif; font-size: 12.8px;">User {USERNAME} ({GROUP}) comment on quarterly report {QUARTER}&nbsp;data program {NAMA_PROGRAM}</span></p>\r\n\r\n<p>comment :<br />\r\n{COMMENT}</p>\r\n', 'QUARTERREPCOMMENT'),
	(35, 'Mitra Submit Quarterly Report', '', '', 'Mitra {MITRA} Submit Quarterly Report Period  Quarter {QUARTER}', '<p><span style="color: rgb(34, 34, 34); font-family: arial, sans-serif; font-size: 12.8px;">Yth</span>,&nbsp;AIPJ</p>\r\n\r\n<p><span style="color: rgb(34, 34, 34); font-family: arial, sans-serif; font-size: 12.8px;">User {USERNAME} dari mitra {MITRA} submit quarterly report period quarter {QUARTER}&nbsp;data program {NAMA_PROGRAM}</span></p>\r\n\r\n<p>klik <a href="{LINK}">disini</a></p>\r\n', 'QUARTERREPMITRA'),
	(36, 'AM Monthly Report ', '', '', 'AM Monthly Report {MONTH}', '<p><span style="color: rgb(34, 34, 34); font-family: arial, sans-serif; font-size: 12.8px;">Yth</span>,&nbsp;PPM</p>\r\n\r\n<p><span style="color: rgb(34, 34, 34); font-family: arial, sans-serif; font-size: 12.8px;">User {USERNAME} submit AM monthly report {MONTH} </span></p>\r\n\r\n<p>klik <a href="{LINK}">disini</a></p>\r\n', 'MONREPAM'),
	(37, 'PPM Submit Quarterly Report', '', '', 'PPM {PPM} Submit Quarterly Report Period  Quarter {QUARTER}', '<p><span style="color: rgb(34, 34, 34); font-family: arial, sans-serif; font-size: 12.8px;">Yth</span>,&nbsp;AIPJ2</p>\r\n\r\n<p><span style="color: rgb(34, 34, 34); font-family: arial, sans-serif; font-size: 12.8px;">User {USERNAME} dari PPM {PPM} submit quarterly report period quarter {QUARTER}&nbsp;data program {NAMA_PROGRAM}</span></p>\r\n\r\n<p>klik <a href="{LINK}">disini</a></p>\r\n', 'QUARTERREPPPM'),
	(38, 'AM Submit Quarterly Report', '', '', 'AM {AM} Submit Quarterly Report Period  Quarter {QUARTER}', '<p><span style="color: rgb(34, 34, 34); font-family: arial, sans-serif; font-size: 12.8px;">Yth</span>,&nbsp;AIPJ2</p>\r\n\r\n<p><span style="color: rgb(34, 34, 34); font-family: arial, sans-serif; font-size: 12.8px;">User {USERNAME} dari AM {AM} submit quarterly report period quarter {QUARTER}&nbsp;data program {NAMA_PROGRAM}</span></p>\r\n\r\n<p>klik <a href="{LINK}">disini</a></p>\r\n', 'QUARTERREPAM'),
	(39, 'Signoff by Team Leader', '', '', 'Signoff by Team Leader Submit Activity Statement', '<p><span style="color: rgb(34, 34, 34); font-family: arial, sans-serif; font-size: 12.8px;">Yth</span>,&nbsp;AIPJ2</p>\n\n<p><span style="color: rgb(34, 34, 34); font-family: arial, sans-serif; font-size: 12.8px;">User {USERNAME} </span><span style="color: rgb(34, 34, 34); font-family: arial, sans-serif; font-size: 12.8px;">submit Activity Statement</span><span style="color: rgb(34, 34, 34); font-family: arial, sans-serif; font-size: 12.8px;"> data program {NAMA_PROGRAM}</span></p>\n', 'ASSIGNOFFBYTL'),
	(40, 'Dashboard Monthly Report ', '', '', 'Dashboard Monthly Report {MONTH}', '<p><span style="color: rgb(34, 34, 34); font-family: arial, sans-serif; font-size: 12.8px;">User {USERNAME} submit Dashboard monthly report {MONTH} </span></p>\r\n\r\n<p>klik <a href="{LINK}">disini</a></p>\r\n', 'MONREPDASBOARD'),
	(41, 'Milestone Report', '', '', 'Milestone Report', '<p><span style="color: rgb(34, 34, 34); font-family: arial, sans-serif; font-size: 12.8px;">Yth</span>,&nbsp;Finance</p>\r\n\r\n<p><span style="color: rgb(34, 34, 34); font-family: arial, sans-serif; font-size: 12.8px;">User {USERNAME} send Delivered Milstone </span></p>\r\n\r\n<p>klik <a href="{LINK}">disini</a></p>\r\n', 'MILSETONEFIN'),
	(42, 'Reminder Partner Monthly Report', '', '', 'Reminder: Laporan Bulanan Segera Jatuh Tempo', '<p><span style="color: rgb(34, 34, 34); font-family: arial, sans-serif; font-size: 12.8px;">Yth</span>,&nbsp;Mitra {MITRA} yang kami hormati,&nbsp;</p>\r\n\r\n<p>Dengan ini kami informasikan bahwa Laporan Bulanan untuk program <strong>{NAMA_PROGRAM}</strong> bulan <strong>{PERIOD}</strong> {HARI}. Mohon segera lengkapi laporan Anda dan kirimkan laporan bulanan melalui sistem.</p>\r\n\r\n<p>klik <a href="{LINK}">disini</a></p>\r\n', 'REMRPTPARTNER'),
	(43, 'Revision AM Monthly Report ', '', '', 'Revision AM Monthly Report {MONTH}', '<p><span style="color: rgb(34, 34, 34); font-family: arial, sans-serif; font-size: 12.8px;">Yth</span>,&nbsp;AM</p>\r\n\r\n<p><span style="color: rgb(34, 34, 34); font-family: arial, sans-serif; font-size: 12.8px;">User {USERNAME} set AM monthly report {MONTH} to Revision </span></p>\r\n\r\n<p>klik <a href="{LINK}">disini</a></p>\r\n', 'MONREPAMREV'),
